var rufus = require('../');

rufus.config({
  handlers: {
    'rot': {
      'class': rufus.handlers.Rotating,
      file: './test.log',
      oldFile: './old.test.%d.log',
      maxFiles: 3,
      timeRate: 1000 * 30 //every minute
    }
  },
  loggers: {
    root: {
      handlers: ['rot']
    }
  }
});

var logger = rufus.getLogger('root');

setInterval(function() {
  logger.info('just test', Date.now());
}, 100);

